typedef long long ll;
typedef pair <ll,ll> P;
typedef pair <ll,P> PP;
int I[100001],n,L;;

bool check(int i){
  if(I[i]==0||i<0||i>=n)return 0;
  return (i==0||I[i-1]<I[i])&&(I[i+1]<I[i]);
}

int main(){

  cin>>n>>L;
  for(int i=0;i<n;i++)cin>>I[i];

  priority_queue<PP> Q;
  for(int i=0;i<n;i++)if(check(i))Q.push(PP(I[i],P(0,i)));

  ll ans=0;

  while(!Q.empty()){
    PP t=Q.top();Q.pop();
    ll T=t.s.f,idx=t.s.s;
    I[idx]+=(ans-T);
    if(I[idx]<L) ans+=(L-I[idx]);
    else {
      if(idx)I[idx-1]+=(I[idx]-L);
      I[idx+1]+=(I[idx]-L);
    }
    I[idx]=0;
    if(check(idx-1)) Q.push(PP(I[idx-1],P(ans,idx-1)));
    if(check(idx+1)) Q.push(PP(I[idx+1],P(ans,idx+1)));
  }
  cout <<ans<<endl;
}

#include<stdio.h>
