using namespace std;

vector<int> V,UP;
int N,L;


int getmax(){
  int out = 0;

  for(int i = 0; i < UP.size(); i++)
    out = max(out,V[UP[i]]);

  return out;
}
int main(){

  cin >> N >> L;


  V.push_back(0); 
  for(int i = 0; i < N; i++){
    int in;
    cin >> in;
    V.push_back(in);
  }
  V.push_back(0);

  int ans = 0;
  while(1){

    UP.clear();
    for(int i = 1; i < V.size()-1; i++)
      if(V[i-1] < V[i] && V[i+1] < V[i])
	UP.push_back(i);

    if(UP.size() == 0) break;

    int maxpos = getmax();
    int add = L-maxpos;
    ans += add;
    for(int i = 0; i < UP.size(); i++){
      if(V[UP[i]] == maxpos) V[UP[i]] = 0;
      else V[UP[i]] += add;
    }
  }
  cout << ans << endl;
  return 0;
}

