#include	<iostream>
#include	<queue>
#include	<vector>
#include	<utility>

using namespace std;

typedef pair<int, int> PAIR;

class Icicles {
private:
	int datas[100002];
	int num;
	int limit;
	bool checkGrowth(int index) {
		if (datas[index] > datas[index + 1] && datas[index] > datas[index - 1]) { return true; }
		return false;
	}
public:
	void data_inserting(int n, int l, vector<int> &order) {
		num = n;
		limit = l;
		datas[0] = 0;
		for (int i = 0; i < order.size(); i++) {
			datas[1 + i] = order[i];
		}
		datas[num + 1] = 0;
	}
	int calc(void) {
		int result = 0, buff;
		PAIR _p, _lf, _ri;

		priority_queue<PAIR> PQ;
		for (int i = 0; i < num; i++) {
			if (checkGrowth(i+1)) { PQ.push(PAIR(datas[i+1], i+1)); }
		}
		while (!PQ.empty()) {
			_p = PQ.top();
			PQ.pop();
			buff = limit - (_p.first + result);
			result += buff > 0 ? buff : 0;
			datas[_p.second] = 0;
			if (checkGrowth(_p.second - 1)) {
				PQ.push(PAIR(datas[_p.second - 1] - result, _p.second - 1));
			}
			if (checkGrowth(_p.second + 1)) {
				PQ.push(PAIR(datas[_p.second + 1] - result, _p.second + 1));
			}
		}
		return result;
	}
};

int main(void) {
	int N, L, buff;
	while (1) {
		vector<int> order;
		Icicles I;

		cin >> N >> L;
		if (cin.eof()) { break; }

		for (int i = 0; i < N; i++) {
			cin >> buff;
			order.push_back(buff);
		}
		I.data_inserting(N, L, order);
		cout << I.calc() << endl;
	}
	return 0;
}

# include <iostream>
# include <algorithm>

# define MAX_N 100000

int n, l;
int a[MAX_N];

int memo[MAX_N];

int Icicle(int i) {
	if (memo[i] > 0)
		return memo[i];

	int res = l - a[i];
	if (i == 0) {
		if (a[i] > a[i + 1])
			return memo[i] = res;
		else
			return memo[i] = res + Icicle(i + 1);
	}
	if (i == (n - 1)) {
		if (a[i] > a[i - 1])
			return memo[i] = res;
		else
			return memo[i] = res + Icicle(i - 1);
	}
	else {
		if (a[i - 1] < a[i] && a[i + 1] < a[i])
			return memo[i] = res;
		else if (a[i - 1] > a[i] && a[i + 1] < a[i])
			return memo[i] = res + Icicle(i - 1);
		else if (a[i - 1]<a[i] && a[i + 1]>a[i])
			return memo[i] = res + Icicle(i + 1);
		else
			return memo[i] = res + Icicle(i + 1) + Icicle(i - 1);
	}
}

int main() {
	std::cin >> n >> l;
	for (int i = 0; i < n; ++i)
		std::cin >> a[i];
	int res = 0;
	for (int i = 0; i < n; ++i) {
		res = std::max(res, Icicle(i));
	}
	std::cout << res << std::endl;
	return 0;
}

