const int INF = INT_MAX;

struct Solver
{
	int n, l;
	VI as, dp, done;

	Solver()
	{
		cin >> n >> l;
		as.resize( n );
		FOR( a, as )
		{
			cin >> a;
		}
		dp.resize( n );
		done.resize( n );

		return;
	}

	operator int()
	{
		int res = 0;
		REP( i, 0, n )
		{
			res = max( res, rec( i ) );
		}
		return res;
	}

	int rec( int x )
	{
		if ( done[x] )
		{
			return dp[x];
		}

		done[x] = true;

		int res = 0;
		if ( x && as[x] < as[ x - 1 ] )
		{
			res = max( res, rec( x - 1 ) );
		}
		if ( x + 1 < n && as[x] < as[ x + 1 ] )
		{
			res = max( res, rec( x + 1 ) );
		}
		return dp[x] = res + ( l - as[x] );
	}
};

int main()
{
	cin.tie( 0 );
	ios::sync_with_stdio( false );

	cout << Solver() << endl;

	return 0;
}

