ll INF=1LL<<58;

class Main{
public:

	bool isTop(int i){
		if(i-1>=0 && as[i-1]>=as[i])return false;
		if(i+1<N && as[i]<=as[i+1])return false;
		return true;
	}

	int N,L;
	vector<ll> as;
	void run(){
	//	ifstream cin("in");
	//	ofstream cout( "out" );
		nextInt(N);nextInt(L);
		as=vector<ll>(N);REP(i,N)nextLong(as[i]);

		priority_queue<pair<ll,int>,vector<pair<ll,int>>,greater<pair<ll,int>>> que;
		// end time, index

		REP(i,N)if(isTop(i)) que.push(make_pair(L-as[i],i));

		ll t=0;
		while(!que.empty()){
			pair<ll,int> task=que.top();que.pop();
			ll end=task.first;int i=task.second;
			t=end;
			as[i]=0;
			if(i-1>=0 && isTop(i-1)) que.push(make_pair(t+L-as[i-1],i-1));
			if(i+1<N && isTop(i+1)) que.push(make_pair(t+L-as[i+1],i+1));
		}

		cout <<t<<endl;
	}
};
int main(){
	cout <<fixed<<setprecision(3);	
	ios::sync_with_stdio(false);
	Main().run();
	return 0;
}

