#include <functional>

using namespace std;

int main() {
    int i;
    int n, l, d, done;
    char flg[100000];
    int a[100000];
    int done_pos[100000];
    priority_queue<pair<int, int>, vector<pair<int, int> >, less<pair<int, int> > > next_broken;

    scanf("%d %d", &n, &l);

    scanf("%d", &a[0]);
    flg[0] = 1;

    for (i=0;i<n;i++) {
        done_pos[i] = 0;
    }

    for (i=1;i<n;i++) {
        scanf("%d", &a[i]);
        flg[i] = a[i-1] < a[i];
        if (flg[i-1] && !flg[i]) next_broken.push(make_pair(a[i-1], i-1));
    }

    if (flg[n-1]) next_broken.push(make_pair(a[n-1], n-1));

    d = 0;
    done = 0;
    while (!next_broken.empty() && done < n) {
        pair<int, int> tmp = next_broken.top();
        int len = tmp.first;
        int pos = tmp.second;

        len += d;
        d += (l - len);
        a[pos] = 0;

        if (!done_pos[pos]) {
            done_pos[pos] = 1;
            done++;
        }

        if ((pos == 1 && a[0] > a[1]) || (pos > 1 && a[pos-1] > a[pos] && a[pos-2] < a[pos-1])) {
            a[pos-1] -= d;
            next_broken.push(make_pair(a[pos-1], pos-1));
        }

        if ((pos == n-2 && a[n-2] < a[n-1]) || (pos < n-2 && a[pos] < a[pos+1] && a[pos+1] > a[pos+2])) {
            a[pos+1] -= d;
            next_broken.push(make_pair(a[pos+1], pos+1));
        }

        next_broken.pop();
    }

    printf("%d\n", d);
}

#include <iostream>
#include <queue>
#include <utility>
using namespace std;

int main(){
	int n,l,*ice,buf,pa,h=0,sz;
	queue<pair<int, int> > q;
	priority_queue<pair<int, int> > bufq;

	cin >> n >> l;

	ice = new int[n+2];
	ice[0] = ice[n+1] = 0;
	for(int i = 1; i < n+1; i++){
		cin >> ice[i];
	}

	for(int i = 1; i < n+1; i++){
		if(ice[i]){
			if((ice[i] > ice[i-1]) && (ice[i] > ice[i+1])){
				bufq.push(make_pair(ice[i], i));
			}
		}
	}

	while(1){
		sz = bufq.size();
		for(int i = 0; i < sz; i++){
			q.push(bufq.top());
			bufq.pop();
		}

		pa = l - q.front().first;

		sz = q.size();
		for(int i = 0; i < sz; i++){
			buf = q.front().second;
			q.pop();

			ice[buf] += pa;
			if(ice[buf] >= l){
				ice[buf] = 0;
				if(ice[buf-1] > ice[buf-2]){
					bufq.push(make_pair(ice[buf-1], buf-1));
				}
				if(ice[buf+1] > ice[buf+2]){
					bufq.push(make_pair(ice[buf+1], buf+1));
				}
			}else{
				bufq.push(make_pair(ice[buf], buf));
			}
		}
		h += pa;

		if(bufq.empty()){
			break;
		}
	}

	cout << h << endl;

	return 0;
}

