                t[j] = max(t[j], t[j+1] + m-l[j]);
            }
            for (int j = i+1; j <= n; j++) {
                if (l[j] > l[j-1]) break;
                t[j] = max(t[j], t[j-1] + m-l[j]);
            }
        }
    }
    ll r = 0;
    for (int i = 1; i <= n; i++) {
        r = max(r, t[i]);
    }
    printf("%lld\n", r);
    return 0;
}

