int dp[100000];

int calc(int pos)
{
	int wait = 0;

	if(dp[pos] != 0)
		return dp[pos];

	if(0 < pos && a[pos] < a[pos - 1])
		wait = max(wait, calc(pos - 1));
	if(pos < n - 1 && a[pos] < a[pos + 1])
		wait = max(wait, calc(pos + 1));

	return dp[pos] = wait + l - a[pos];
}

