#include <cstring>

using namespace std;

int main()
{
	int n, lim, i, remain;
	int trr[100002];
	int falltime[100002];


	while( !cin.eof() )
	{
		memset( trr, 0, sizeof(trr));
		memset( falltime, -1, sizeof(falltime));
		cin >> n >> lim;

		for( i = 1; i <= n; i++)
		{
			cin >> trr[i];
		}

		remain = n;

		for( i = 1; i <= n; i++)
			if( trr[i] > trr[i-1] && trr[i] > trr[i+1] )
			{
				falltime[i] = lim - trr[i];
				remain--;
			}

		while( remain != 0 )
		{
			for( i = 1; i <= n; i++)
			{
				int left = 0, right = 0;
				if( falltime[i] == -1 )
				{
					if( trr[i-1] > trr[i] )
						if( falltime[i-1] != -1 )
							left = falltime[i-1];
						else
							continue;

					if( trr[i+1] > trr[i] )
						if( falltime[i+1] != -1 )
							right = falltime[i+1];
						else
							continue;

					if( left || right )
					{
						falltime[i] = lim - trr[i] + max( left, right );
						remain--;
					}
				}
			}
		}

		int out = 0;
		for( i = 1; i <= n; i++)
		{
			out = max(out, falltime[i]);
		}

		cout << out << endl;
	}

	return 0;
}

