    for(int i=0;i<N;i++)
        cin>>trr[i];

    dp[0]=L-trr[0];
    for(int i=1;i<N;i++){
        dp[i]=L-trr[i];
        if(trr[i-1]>trr[i])dp[i]+=dp[i-1];
        Max=max(Max,dp[i]);
    }

    dp[N-1]=L-dp[N-1];
    for(int i=N-2;i>=0;i--){
        dp[i]=L-trr[i];
        if(trr[i+1]>trr[i])dp[i]+=dp[i+1];
        Max=max(Max,dp[i]);
    }

    cout<<Max<<endl;



