using namespace std;

const int INF = 100000000;

int n, l;
int data[2][100000];
int val[100000];

main(){
  cin >> n >> l;
  for(int i=0;i<n;i++){
    cin >> data[0][i];
    val[i] = data[0][i];
  }
  int ans = 0;
  int rem = n;
  int now = 0;
  int next = 1;
  while(rem > 0){
    sort(val, val + n);
    int valm = l - val[n-1];
    if(data[now][0] > data[now][1]){
      data[next][0] = data[now][0] + valm;
      if(data[next][0] >= l){
        data[next][0] = 0;
        --rem;
      }
    }else{
      data[next][0] = data[now][0];
    }
    val[0] = data[next][0];
    for(int i=1;i<n-1;i++){
      if(data[now][i-1] < data[now][i] && data[now][i] > data[now][i+1]){
        data[next][i] = data[now][i] + valm;
        if(data[next][i] >= l){
          data[next][i] = 0;
          --rem;
        }
      }else{
        data[next][i] = data[now][i];
      }
      val[i] = data[next][i];
    }
    if(data[now][n-2] < data[now][n-1]){
      data[next][n-1] = data[now][n-1] + valm;
      if(data[next][n-1] >= l){
        data[next][n-1] = 0;
        --rem;
      }
    }else{
      data[next][n-1] = data[now][n-1];
    }
    val[n-1] = data[next][n-1];
    next = now;
    now = 1 - next;
    ans += valm;
  }
  cout << ans << endl;
  return 0;
}

