int sol() {
	bool flg = true;
	for(int i=0;i<n;i++) {
		if(a[i]>0){
			flg=false;
			break;
		}
	}
	if(flg)return 1;
	if(a[0]>a[1]) {
		a[0]++;
		if(a[0]>=l)a[0]=0;
	}
	if(a[n-1]>a[n-2]) {
		a[n-1]++;
		if(a[n-1]>=l)a[n-1]=0;
	}
	for(int i=1;i<n-1;i++) {
		if(a[i]>a[i-1]&&a[i]>a[i+1]){
			a[i]++;
			if(a[i]>=l)a[i]=0;
		}
	}

	return 1+sol();
}

