using namespace std;

typedef pair<int,int>P;

const int MAX_N = 100005;
const int MAX_L = 500001;

int a[MAX_N];
int k[MAX_N];
multimap<int,int> mp;
ll t[MAX_N];
int n,L;

int main()
{
	scanf("%d%d",&n,&L);
	rep(i,n){
		scanf("%d",&a[i]);
		k[i] = a[i];
		mp.emplace(a[i],i);
	}
	sort(a,a+n,greater<int>());
	int id = 0;
	while(id<n){
		auto p = mp.equal_range(a[id]);
		auto it = p.first;
		int i = 0;
		while(it != p.second){
			int prev = it->second;
			if(prev!=0&&prev!=n-1){
				if(k[prev] > k[prev-1] && k[prev] > k[prev+1]){
					t[prev] = L-a[id];
				}else if(k[prev] < k[prev-1] && k[prev] > k[prev+1]){
					t[prev] = t[prev-1] + L-a[id];
				}else if(k[prev] > k[prev-1] && k[prev] < k[prev+1]){
					t[prev] = t[prev+1] + L-a[id];
				}else{
					t[prev] = max(t[prev-1],t[prev+1])+L-a[id];
				}
			}else if(prev==0){
				if(k[0] > k[1]){
					t[0] = L-a[id];
				}else{
					t[0] = t[1] + L-a[id];
				}
			}else{
				if(k[n-1] > k[n-2]){
					t[n-1] = L-a[id];
				}else{
					t[n-1] = t[n-2] + L-a[id];
				}
			}
			it++;
			i++;
		}
		id += i;
	}
	printf("%lld\n",*max_element(t,t+n));
	return 0;
}

