        }

        ret += (l - max_);

        for(int i = 0; i < n; i++)
        {
            bool ok2 = true;

            if(i + 1 != n)
            {
                ok2 = ok2 & (a[i] > a[i + 1]);
            }

            if(i != 0)
            {
                ok2 = ok2 & (a[i] > a[i - 1]);
            }

            if(ok2)
            {
                a[i] += (l - max_);
            }
        }

        for(int i = 0; i < n; i++)
        {
            if(a[i] == l)
            {
                a[i] = 0;
            }
        }
    }

    printf("%lld\n", ret);

    return 0;
}

