	ice = new int[n+2];
	ice[0] = ice[n+1] = 0;
	for(int i = 1; i < n+1; i++){
		cin >> ice[i];
	}

	for(int i = 1; i < n+1; i++){
		if(ice[i]){
			if((ice[i] > ice[i-1]) && (ice[i] > ice[i+1])){
				q.push(i);
			}
		}
	}

	while(1){
		buf = q.front();
		prog = (l - ice[buf]) > 0?(l - ice[buf]):0;

		sz = q.size();
		for(int i = 0; i < sz; i++){
			buf = q.front();
			q.pop();

			ice[buf] += prog;
			over = (ice[buf] - l) >= 0?(ice[buf] - l):-1;
			if(over != -1){
				ice[buf] = 0;
				if(ice[buf-1] > ice[buf-2]){
					ice[buf-1] += over;
					q.push(buf-1);
				}
				if(ice[buf+1] > ice[buf+2]){
					ice[buf+1] += over;
					q.push(buf+1);
				}
			}else{
				q.push(buf);
			}
		}
		h += prog;

