int main() {

    ll n, l; cin >> n >> l;
    vector<ll> a(n); 
    for (ll i = 0; i < n; i++) cin >> a[i];

    vector<ll> rem(n, 0), r(n), s(n, 0x7FFFFFFF), t(n);
    for (ll i = 0; i < n; i++) {
        if (i && a[i] <= a[i-1]) rem[i]++;
        if (i < n-1 && a[i] <= a[i+1]) rem[i]++;
        r[i] = l - a[i];
        if (!rem[i]) {
            s[i] = 0;
            t[i] = s[i] + r[i];
        }
    }
    auto compf = [&](ll i, ll j) -> bool {
        return t[i] > t[j];
    };
    priority_queue<ll, vector<ll>, decltype(compf)> q(compf);

    for (ll i = 0; i < n; i++) {
        if (!rem[i]) {
            q.push(i);
        }
    }

    while (!q.empty()) {
        ll i = q.top(); q.pop();
        if (i-1 >= 0 && rem[i-1]) {
            rem[i-1]--;
            if (!rem[i-1]) {
                s[i-1] = t[i];
                t[i-1] = s[i-1] + r[i-1];
                q.push(i-1);
            }
        }
        if (i+1 < n && rem[i+1]) {
            rem[i+1]--;
            if (!rem[i+1]) {
                s[i+1] = t[i];
                t[i+1] = s[i+1] + r[i+1];
                q.push(i+1);
            }
        }
    }

    cout << *max_element(t.begin(), t.end()) << endl;

}

