#define NCLR(mat) memset(mat, -1, sizeof(mat))

using namespace std;

typedef pair<int,int> paii;
typedef long long ll;

int N, L;
int icicle[100002];
int dp[100002];

int solve_rec(int p)
{
	if(dp[p] != 0)
		return dp[p];

	int res = L - icicle[p];
	int left = 0, right = 0;

	if(p < N+1 && icicle[p] < icicle[p+1])
	{
		right = solve_rec(p+1);
	}

	if(p > 0 && icicle[p] < icicle[p-1])
	{
		left = solve_rec(p-1);
	}

	res += max(left, right);

	dp[p] = res;
	return res;
}
int solve()
{
	int res = 0;

	FOR(i, 0, N)
	{
		res = max(solve_rec(i), res);
	}

	return res;
}

