    if(data[now][0] < data[now][1]) valm = min(valm, l-data[now][0]);
    for(int i=1;i<n-1;i++){
      if(data[now][i-1] < data[now][i] && data[now][i] > data[now][i+1]){
        valm = min(valm, l-data[now][i]);
      }
    }
    if(data[now][n-2] < data[now][n-1]) valm = min(valm, l-data[now][n-1]);
    int pre = data[now][0];
    if(data[now][0] > data[now][1]){
      data[now][0] += valm;
      if(data[now][0] >= l){
        data[now][0] = 0;
        --rem;
      }
    }
    for(int i=1;i<n-1;i++){
      if(pre >= data[now][i] || data[now][i] <= data[now][i+1]){
        pre = data[now][i];
        continue;
      }
      pre = data[now][i];
      data[now][i] += valm;
      if(data[now][i] >= l){
        data[now][i] = 0;
        --rem;
      }
    }
    if(pre < data[now][n-1]){
      data[now][n-1] += valm;
      if(data[now][n-1] >= l){
        data[now][n-1] = 0;
        --rem;
      }
