void make_pr_queue(struct node *array, int n){
  int i,tmp;
  struct node swaps;

  for(i=n-1;i>=0;i--){
    if(2*i+1>=n) continue;
    if(2*i+2<n && array[2*i+2].l>array[2*i+1].l) tmp=2*i+2;
    else tmp=2*i+1;
    if(array[tmp].l>array[i].l){
      swaps=array[i];
      array[i]=array[tmp];
      array[tmp]=swaps;
    }
  }
}

struct node pop_pr_queue(struct node *array,int n){
  struct node ret;
  ret=array[0];
  array[0]=array[n-1];
  make_pr_queue(array,n-1);
  return ret;
}

