import java.util.Comparator;
import java.util.PriorityQueue;
import java.util.Queue;

/**
 * Icicles
 */
public class Main {

	static P0551 main = new P0551();

	public static void main(String[] args) throws IOException {

		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String line = "";

		while ((line = br.readLine()) != null && !line.isEmpty()) {

			int n = Integer.parseInt(line.substring(0, line.indexOf(' ')));
			int l = Integer.parseInt(line.substring(line.indexOf(' ') + 1));

			Icicle[] icicles = new Icicle[n + 2];
			Queue<Icicle> growths = new PriorityQueue<Icicle>(1,
					new Comparator<Icicle>() {
						@Override
						public int compare(Icicle o1, Icicle o2) {
							return o2.length - o1.length;
						}
					});

			// read
			icicles[0] = main.new Icicle(0);
			for (int i = 1; i < n + 1; i++) {
				icicles[i] = main.new Icicle(Integer.parseInt(br.readLine()));
			}
			icicles[n + 1] = main.new Icicle(0);

			// solve

			int total = 0;
			while (true) {

				for (int i = 1; i < n + 1; i++) {
					if (!icicles[i].queued
							&& icicles[i].length > icicles[i - 1].length
							&& icicles[i].length > icicles[i + 1].length) {
						icicles[i].queued = true;
						growths.offer(icicles[i]);
					}
				}

				if (growths.isEmpty()) {
					break;
				}

				//
				int elapse = l - growths.peek().length;
				total += elapse;

				while (!growths.isEmpty()) {
					if (growths.peek().length + elapse == l) {
						growths.peek().length = 0;
						growths.poll();
					} else {
						break;
					}
				}

				for (Icicle i : growths) {
					i.length += elapse;
				}

			}
			System.out.println(total);
		}
	}

	class Icicle {
		int length;
		boolean queued = false;

		Icicle(int length) {
			this.length = length;
		}
	}
}

import java.util.ArrayList;
import java.util.Scanner;
import java.util.Stack;

public class Main {

	static Scanner sc = new Scanner(System.in);

	public static void main(String[] args) {
		int N = sc.nextInt();
		int L = sc.nextInt();
		Node[] nodes = new Node[N + 2];
		for (int i = 0; i < N; ++i) {
			nodes[i + 1] = new Node(L - sc.nextInt());
		}
		nodes[0] = nodes[N + 1] = new Node(0);
		Node root = new Node(0);
		for (int i = 1; i <= N; ++i) {
			boolean left = nodes[i - 1].v > nodes[i].v;
			if (left) {
				nodes[i - 1].child.add(nodes[i]);
			}
			boolean right = nodes[i + 1].v > nodes[i].v;
			if (right) {
				nodes[i + 1].child.add(nodes[i]);
			}
			if (!left && !right) {
				root.child.add(nodes[i]);
			}
		}
		Stack<Frame> stack = new Stack<Frame>();
		stack.push(new Frame(root, 0));
		long ret = 0;
		while (!stack.isEmpty()) {
			Frame f = stack.pop();
			if (f.i != 0) {
				f.t = Math.max(f.t, ret);
			}
			if (f.i == f.n.child.size()) {
				ret = f.t + f.n.v;
				continue;
			}
			stack.add(new Frame(f.n, f.i + 1));
			stack.add(new Frame(f.n.child.get(f.i), 0));
		}
		System.out.println(ret);
	}

	static class Frame {
		Node n;
		int i;
		long t = 0;

		Frame(Node n, int i) {
			this.n = n;
			this.i = i;
		}
	}

	static class Node {

		long v;
		ArrayList<Node> child = new ArrayList<Node>();

		Node(int v) {
			this.v = v;
		}

	}

}

