            for (int i = 0; i < n; i++)
            {
                if ((i == 0 && lengthes[i] > lengthes[i + 1]) ||
                    (i == n - 1 && lengthes[i] > lengthes[i - 1]) ||
                    (i > 0 && i < n - 1 && lengthes[i] > lengthes[i - 1] &&
                    lengthes[i] > lengthes[i + 1]))
                {
                    que.Enqueue(l - lengthes[i], i);
                }
            }

            while (que.Count > 0)
            {
