public class Ice
{
    public int L { get; set; }
    public int p { get; set; }
}

public class Hello
{
    public static void Main()
    {
        var ices = new List<Ice>();
        string[] line = Console.ReadLine().Trim().Split(' ');
        var n = int.Parse(line[0]);
        var L = int.Parse(line[1]);
        var a = new int[n, 2];
        for (int i = 0; i < n; i++)
        {
            var w = int.Parse(Console.ReadLine().Trim());
            a[i, 0] = w;
            ices.Add(new Ice { p = i, L = w });
        }
        var ret = getAnswer(a, ices, L);
        Console.WriteLine(ret);
    }
    public static int getAnswer(int[,] a, List<Ice> ices, int L)
    {
        var n = a.GetLength(0);
        var ret = 0;
        foreach (var x in ices.OrderByDescending(y => y.L))
        {
            if (x.p == 0 && x.L > a[1, 0]) { a[0, 1] = L - x.L; ret = Math.Max(a[0, 1], ret); }
            else if (x.p == 0 && x.L < a[1, 0]) { a[0, 1] = L - x.L + a[1, 1]; ret = Math.Max(a[0, 1], ret); }
            else if (x.p == n - 1 && x.L > a[n - 2, 0]) { a[n - 1, 1] = L - x.L; ret = Math.Max(a[n - 1, 1], ret); }
            else if (x.p == n - 1 && x.L < a[n - 2, 0]) { a[n - 1, 1] = L - x.L + a[n - 2, 1]; ret = Math.Max(a[n - 1, 1], ret); }
            else if (x.L > a[x.p - 1, 0] && x.L > a[x.p + 1, 0]) { a[x.p, 1] = L - x.L; ret = Math.Max(a[x.p, 1], ret); }
            else if (x.L > a[x.p - 1, 0] && x.L < a[x.p + 1, 0]) { a[x.p, 1] = L - x.L + a[x.p + 1, 1]; ret = Math.Max(a[x.p, 1], ret); }
            else if (x.L < a[x.p - 1, 0] && x.L > a[x.p + 1, 0]) { a[x.p, 1] = L - x.L + a[x.p - 1, 1]; ret = Math.Max(a[x.p, 1], ret); }
            else { a[x.p, 1] = L - x.L + Math.Max(a[x.p + 1, 1], a[x.p - 1, 1]); ret = Math.Max(a[x.p, 1], ret); }
        }
        return ret;
    }
}

