def solve(trr, N):
#    print trr
    if N == 1:
        return L - trr[0]
    elif N == 2:
        return (L - trr[0]) + (L - trr[1])
    best_hour = -1
    peaks = []
    if trr[0] > trr[1]:
        peaks.append(0)
    if trr[N-1] > trr[N-2]:
        peaks.append(N-1)
    peaks += [i for i in xrange(1, N-1) if trr[i-1] < trr[i] > trr[i+1]]
#    print peaks
    for i, p in enumerate(peaks):
        hour1 = hour2 = L - trr[p]
        p_tm1 = p
        while p_tm1+1 < N and trr[p_tm1] > trr[p_tm1+1]:
            hour1 += L - trr[p_tm1+1]
