def solve(trr, hour=0):
    N = len(trr)
    if N == 0:
        return hour
    elif N == 1:
        trr[0] += 1
    elif N == 2:
        if trr[0] > trr[1]:
            trr[0] += 1
        else:
            trr[1] += 1
    elif N > 2:
        if trr[0] > trr[1]:
            trr[0] += 1
        if trr[N-1] > trr[N-2]:
            trr[N-1] += 1
        for i in xrange(1, N-1):
            if N > 2 and trr[i-1] < trr[i] > trr[i+1]:
                trr[i] += 1
    return solve([t for t in trr if t < 6], hour+1)


