int plate[5002][5002];

int n, m;

int main()
{
	memset(plate, 0, sizeof(plate));
	scanf("%d %d", &n, &m);
	for (int i = 0; i < m; ++i)
	{
		int a, b, x;
		scanf("%d %d %d", &a, &b, &x);
		--a; --b;
		++plate[a][b];
		--plate[a][b + 1];
		--plate[a + x + 1][b];
		++plate[a + x + 2][b + 1];
		++plate[a + x + 1][b + x + 2];
		--plate[a + x + 2][b + x + 2];
	}
	for (int i = 1; i <= n + 2; ++i)
	{
		for (int j = 1; j <= n + 2; ++j)
		{
			plate[i][j] += plate[i - 1][j - 1];
		}
	}
	for (int i = 0; i <= n + 2; ++i)
	{
		for (int j = 1; j <= n + 2; ++j)
		{
			plate[i][j] += plate[i][j - 1];
		}
	}
	for (int i = 1; i <= n + 2; ++i)
	{
		for (int j = 0; j <= n + 2; ++j)
		{
			plate[i][j] += plate[i - 1][j];
		}
	}

	int ans = 0;
	for (int i = 0; i <= n + 2; ++i)
	{
		for (int j = 0; j <= n + 2; ++j)
		{
			if (plate[i][j] > 0)++ans;
		}
	}

	printf("%d\n",ans);
	return 0;
}

