        int a, b, x;
        cin >> a >> b >> x;
        update(n, a, b, x);
    }
    resolve(n);
    int ans = 0;
    proc(n, [&](int i, int j) { ans += g[i][j] > 0; });
    cout << ans << endl;
    return 0;
}

