set<int>::iterator gt(set<int>& bst, int v) { return bst.upper_bound(v); }
set<int>::iterator ge(set<int>& bst, int v) { return bst.lower_bound(v); }
set<int>::iterator lt(set<int>& bst, int v) { return bst.lower_bound(v) == bst.begin() ? bst.end() : --bst.lower_bound(v); }
set<int>::iterator le(set<int>& bst, int v) { return bst.upper_bound(v) == bst.begin() ? bst.end() : --bst.upper_bound(v); }
map<int, int>::iterator gt(map<int, int>& bst, int v) { return bst.upper_bound(v); }
map<int, int>::iterator ge(map<int, int>& bst, int v) { return bst.lower_bound(v); }
