signed main() {
	int n, m;

	scanf("%d %d", &n, &m);

	vector<vector<int>> tri(n+3, vector<int>(n+3,0));

	REP(i, m) {
		int a, b, c;
		scanf("%d %d %d", &a, &b, &c);
		a--; b--;
		tri[a][b] += 1;
		tri[a][b + 1] += -1;
		tri[a + c + 1][b] += -1;
		tri[a + c + 2][b+1] += 1;
		tri[a + c + 1][b+c+2] += 1;
		tri[a + c + 2][b +c+2] += -1;
	}
	REP(i, n+2) {
		REP(j, n+2) {
			tri[i][j + 1] += tri[i][j];
		}
	}
	REP(i, n + 2) {
		REP(j, n + 2) {
			tri[j+1][i] += tri[j][i];
		}
	}
	REP(i, n + 2) {
		REP(j, n + 2) {
			tri[i+1][j + 1] += tri[i][j];
		}
	}
	int ans = 0;
	REP(i, n) {
		REP(j, n) {
			ans += (bool)tri[i][j];
		}
	}
	printf("%d\n", ans);
	return 0;
}

