int main(void){
	scanf("%d%d",&n,&m);
	for(int i=0;i<m;i++){
		int x,y,size;
		scanf("%d%d%d",&y,&x,&size);
		size++;
		tri[x][y]+=1;
		tri[x+1][y]-=1;
		tri[x][y+size]-=1;
		tri[x+1][y+size+1]+=1;
		tri[x+size+1][y+size]+=1;
		tri[x+size+1][y+size+1]-=1;
	}
	for(int i=1;i<=n;i++){
		for(int j=2;j<=n;j++){
			tri[j][i]+=tri[j-1][i];
		}
	}
	for(int i=1;i<=n;i++){
		for(int j=2;j<=n;j++){
			tri[i][j]+=tri[i][j-1];
		}
	}
	for(int i=1;i<=n;i++){
		int y=2,x=i+2;
		while(x<=n){
			tri[x][y]+=tri[x-1][y-1];
			y++,x++;
		}
	}
	for(int i=1;i<=n;i++){
		int y=i+1,x=2;
		while(y<=n){
			tri[x][y]+=tri[x-1][y-1];
			y++,x++;
		}
