// AOJ 0574 (http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=0574)
#include<cstdio>
#define rep(i,a) for(int i=0;i<(a);++i)

const int MAX_N = 5000;

int N, M;
int S[MAX_N+3][MAX_N+1];

int main()
{
	scanf( "%d%d", &N, &M );
	rep( i, M )
	{
		int A, B, X;
		scanf( "%d%d%d", &A, &B, &X );
		--A; --B;
		++S[A][B]; --S[A][B+1];
		--S[A+X+1][B]; ++S[A+X+1][B+X+2];
		++S[A+X+2][B+1]; --S[A+X+2][B+X+2];
	}

	rep( i, N ) rep( j, N )
		S[i][j+1] += S[i][j];
	rep( j, N ) rep( i, N )
		S[i+1][j] += S[i][j];
	rep( i, N ) rep( j, N )
		S[i+j+1][j+1] += S[i+j][j];

	int ans = 0;
	rep( i, N ) rep( j, N ) if( S[i][j] )
		++ans;

	printf( "%d\n", ans );

	return 0;
}

