int main(){
	int N,M,a,b,x,res;short cnt,nkr,ima;
	pair<short,short> pa;
	cin>>N>>M;
	for(int i=0;i<M;i++){
		cin>>a>>b>>x;
		for(int j=0;j<=x;j++){
			pq[a+j].push(MP(b,j));
		}
	}
	res=0;
	for(int i=1;i<=N;i++){
		cnt=nkr=ima=0;
		while(!pq[i].empty()){
			pa=pq[i].top();pq[i].pop();
