        //dfs(x,y,a);
    }
    for(int i=1;i<=n;i++){
        for(int j=1;j<=i;j++){
            dp[i+1][j+1]=max(dp[i+1][j+1],(short)(dp[i][j]-1));
            dp[i+1][j]=max(dp[i+1][j],(short)(dp[i][j]-1));
        }
    }
    int cnt=0;
    for(int i=1;i<=n;i++){
        for(int j=1;j<=i;j++){
            if(dp[i][j]>=0)cnt++;
        }
    }
    cout<<cnt<<endl;
}

