		x[a+c+1][b+c+1]--;
	}
	for(int i=0;i<N;i++){
		for(int j=1;j<N;j++){
			if(j>=i+3){break;}
			x[i][j]+=x[i][j-1];
		}
	}
	for(int j=0;j<N;j++){
		for(int i=1;i<N;i++){
			if(j>=i+3){break;}
			x[i][j]+=x[i-1][j];
		}
	}
	for(int i=0;i<=n;i++){
		for(int j=0;j<=n-i;j++){
			if(j>=(i+j+1)+3){break;}
			x[i+j+1][j+1]+=x[i+j][j];
		}
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			if(x[i][j]>=1){cnt++;}
		}
