using namespace std;

#define N 2020

vector<int> x[N];

int n,m,a,b,c,cnt;
int V[N];

int main(){
	for(int i=0;i<N;i++){
		for(int j=0;j<i+5;j++){
			x[i].push_back(0);
			V[i]=j;
		}
	}
	cin>>n>>m;
	for(int i=0;i<m;i++){
		cin>>a>>b>>c;c++;
		x[a][b]++;
		x[a+c][b]--;
		x[a+c+1][b+1]++;
		x[a][b+1]--;
		x[a+c][b+c+1]++;
		x[a+c+1][b+c+1]--;
	}
	for(int i=0;i<N;i++){
		for(int j=1;j<N;j++){
			if(j<V[i]){
				x[i][j]+=x[i][j-1];
			}
		}
	}
	for(int j=0;j<N;j++){
		for(int i=1;i<N;i++){
			if(j<V[i]){
				x[i][j]+=x[i-1][j];
			}
		}
	}
	for(int i=0;i<=n;i++){
		for(int j=0;j<=n-i;j++){
			if(j+1<V[i+j+1]){
				x[i+j+1][j+1]+=x[i+j][j];
			}
		}
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			if(j<V[i]){
				if(x[i][j]>=1){cnt++;}
			}
		}
	}
	cout<<cnt<<endl;
	return 0;
}

