	scanf("%d%d",&a,&b);
	for(int i=0;i<b;i++){
		int c,d,e;scanf("%d%d%d",&c,&d,&e);
		Q[i]=make_pair(make_pair(c,d),e);
	}std::sort(Q,Q+b);
	int ret=0;
	int at=0;
	for(int i=0;i<a;i++){
		int e=i&1;
		while(at<b&&Q[at].first.first==i+1){
			val[e][Q[at].first.second-1]=max(val[e][Q[at].first.second-1],Q[at].second+1);
			at++;
		}
		for(int j=0;j<=i;j++)if(val[e][j])ret++;
		for(int j=0;j<=i+1;j++)val[!e][j]=0;
		for(int j=0;j<=i;j++){
			val[!e][j]=max(val[!e][j],val[e][j]-1);
			val[!e][j+1]=max(val[!e][j+1],val[e][j]-1);
		}
	}
	printf("%d\n",ret);
}

