			if(j >= Max || j + i >= Max){ break; }
			s += data[j][i+j];
			data[j][i+j] = s;
		}
	}
	//debug();
	int ans = 0;
	for(int i=0;i<Max;i++){
		for(int j=0;j<Max;j++){
			if(data[i][j] > 0){
				ans++;
			}
		}
	}
	printf("%d\n",ans);
}

