int n, m, a, b, c, imos[5009][5009], imos2[5009][5009], imos3[5009][5009];
int main() {
	scanf("%d%d", &n, &m);
	for (int i = 0; i < m; i++) {
		scanf("%d%d%d", &a, &b, &c); a--, b--;
		// cover (a, b), (a+1, b),..., (a+c, b)
		imos2[a][b]++;
		imos2[a + c + 1][b]--;
		// cover (a, b+1), (a+1, b+2),..., (a+c, b+c+1)
		imos3[a][b + 1]++;
		imos3[a + c + 1][b + c + 2]--;
	}
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			imos2[i + 1][j] += imos2[i + 1][j] + imos2[i][j];
		}
	}
	for (int i = -1; i < n; i++) {
		for (int j = 1; i + j <= n; j++) {
			if (i + j <= 0) continue;
			imos3[i + j][j] += imos3[i + j - 1][j - 1];
		}
	}
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			imos[i][j] += imos2[i][j];
			imos[i][j] -= imos3[i][j];
		}
	}
	int ret = 0;
	for (int i = 0; i < n; i++) {
		int sum = 0;
		for (int j = 0; j <= i; j++) {
			sum += imos[i][j];
			if (sum > 0) ret++;
		}
	}
	printf("%d\n", ret);
	return 0;
}

