					case 1: row[x]--;break;
					case 2: dia[y-x+1]++;break;
					case 3: dia[y-x+1]--;break;
				}
				cur++;
			}
			sum+=row[x]+dia[y-x+1];
			ans+=sum>0;
		}
	}
	cout<<ans<<endl;
}

