    d[a+x+1][b]   -= 1;
    d[a+x+2][b+1] += 1;
    d[a+x+1][b+x+2] += 1;
    d[a+x+2][b+x+1+1] -= 1;
  }

  for (int i=0; i<=N; i++) {
    for (int j=0; j<=N; j++) {
