using namespace std;

int main(){
	int N, M;;
	scanf("%d %d", &N, &M);
	vector<vector<int> > memo(N + 2, vector<int>(N + 2, 0));
	for(int i = 0; i < M; i++){
		int a, b, x;
		scanf("%d %d %d", &a, &b, &x);
		a--; b--;
		memo[a][b]++;
		memo[a][b + 1]--;
		memo[a + x + 1][b]--;
		memo[a + x + 1][b + x + 2]++;
		memo[a + x + 2][b + 1]++;
		memo[a + x + 2][b + x + 2]--;
	}
	int res = 0;
	for(int i = 0; i < N + 2; i++){
		for(int j = 0; j < N + 2; j++){
			if(j > 0) memo[i][j] += memo[i][j - 1];
		}
	}
	for(int i = 0; i < N + 2; i++){
		for(int j = 0; j < N + 2; j++){
			if(i > 0) memo[i][j] += memo[i - 1][j];
		}
	}
	for(int i = 0; i < N + 2; i++){
		for(int j = 0; j < N + 2; j++){
			if(i > 0 && j > 0) memo[i][j] += memo[i - 1][j - 1];
			if(memo[i][j] > 0) res++;
		}
	}
	printf("%d\n", res);
	return 0;
}

