  for(int i = 0; i < N; ++i) {
    for(int j = 0, s = 0; j < N; ++j,s = max(0,s-1)) {
      if(G[i][j]) {
	s = max(s, G[i][j]);
      }
      if(s) ++ans;
    }
  }
  cout << ans << endl;
  return 0;
}

