struct tile{
  int u,d;
  tile(){}
  tile(int u,int d) : u(u),d(d) {}
};
struct edge{
  int a,b,x;
  edge(){}
  edge(int a,int b,int x) : a(a), b(b), x(x) {}
  bool operator < (const edge& e)const{
    if( a == e.a ){
      if( b==e.b ){
	return x < e.x;
      }
      return b > e.b;
    }
    return a < e.a;
  }
};
int N,M;
edge E[100001];
vector<tile> T[5001];
int main(){
  scanf("%d %d",&N,&M);
