  for(int i=0;i<M;i++){
    for(int k=0,j=E[i].b;j<=E[i].b+E[i].x;j++,k++){
      int a = E[i].a+k, b = E[i].b+k, x = E[i].x-k;
      // printf("%d %d %d\n",a,b,x);
      if( T[b].empty() ){
	T[b].push_back( tile( a, a+x ) );
      } else {
	if( T[b].back().d+1 < a){
	  T[b].push_back( tile(a, a+x) );
	} else if( T[b].back().d < a+x ){
	  T[b].back().d = a+x;
	}
      }
    }
  }
  int res = 0;
  for(int i=1;i<=5000;i++){
    if( T[i].empty() ) continue;
    for(int j=0;j<T[i].size();j++){
      //   printf("%d  u %d -> d %d\n",i,T[i][j].u,T[i][j].d);
