int N, M, A[500100], B[500100], X[500100];

int imos[5009][5009];

int main() {
    // 入力
    cin >> N >> M;
    REP(i, M) cin >> A[i] >> B[i] >> X[i];

    // imos を初期化する
    REP(i, 5009) {
        REP(j, 5009) {
            imos[i][j] = 0;
        }
    }

    // 左 -> 右, 右上 -> 左下, 右下 -> 左上
    REP(i, M) {
        imos[A[i]        - 1][B[i]        - 1]--;
        imos[A[i]        - 1][B[i]           ]++;
        imos[A[i] + X[i]    ][B[i] + X[i] + 1]--;
        imos[A[i] + X[i] + 1][B[i] + X[i] + 1]++;
        imos[A[i] + X[i] + 1][B[i]           ]--;
        imos[A[i] + X[i]    ][B[i]        - 1]++;
    }

    // 左 -> 右
    FOR(i, 0, N+1) {
        FOR(j, 1, i+1) {
            imos[i][j] += imos[i][j-1]; 
        }
    }
    // 右上 -> 左下
    FOR(i, 1, N+1) {
        FOR(j, 0, i+1) {
            imos[i][j] += imos[i-1][j];
        }
    }
    // 右下 -> 左上
    FORD(i, N, 0) {
        FORD(j, i, 0) {
            imos[i][j] += imos[i+1][j+1];
        }
