int n, m;
int nails[2][5001];
int ans;
map< pair<int, int> , int> tops;
vector<pair<int, int> > toppoints;

int main(){
  scanf("%d%d", &n, &m);
  for(int i = 0;i < m;i++){
    int a, b, x;
    scanf("%d%d%d", &a, &b, &x);
    a--;b--;
    toppoints.push_back(make_pair(a, b));
    tops[make_pair(a, b)] = x + 1;
  }
  sort(toppoints.begin(), toppoints.end(), greater<pair<int, int> >());
  for(int i = 0;i < n;i++){
    for(int j = 0;j < n;j++){
      if(toppoints.back() == make_pair(i, j)){
	nails[i % 2][j] = tops[make_pair(i, j)];
	toppoints.pop_back();
      }
      if(nails[i % 2][j] == 0)continue;
      ans++;
      nails[(i + 1) % 2][j] = max(nails[(i + 1) % 2][j], nails[i % 2][j] - 1);
      nails[(i + 1) % 2][j + 1] = max(nails[(i + 1) % 2][j + 1], nails[i % 2][j] - 1);
    }
  }
  printf("%d\n", ans);
  return 0;
}

