#include <iostream>

#define L 5002

using namespace std;

short m1[L*(L+1)/2] = {0}, m2[L*(L+1)/2] = {0};
int P(int y, int x) {
	++y;
	return y*(y+1)/2 + x;
}
int main() {
	int N, M; cin >> N >> M;
	for (int i = 0; i < M; ++i) {
		int y, x, l; cin >> y >> x >> l; --x, --y, ++l;
		++m1[P(y, x)], --m1[P(y+l, x)];
		--m2[P(y, x+1)], ++m2[P(y+l, x+l+1)];
	}
	for (int y = 0; y < N; ++y) {
		for (int x = 0; x <= y; ++x) {
			m1[P(y+1, x)] += m1[P(y, x)];
			m2[P(y+1, x+1)] += m2[P(y, x)];
		}
	}
	for (int y = 0; y < N; ++y) {
		for (int x = 0; x <= y; ++x) {
			m1[P(y, x)] += m2[P(y, x)];
		}
	}
	for (int y = 0; y < N; ++y) {
		for (int x = 0; x <= y; ++x) {
			m1[P(y, x+1)] += m1[P(y, x)];
		}
	}
	int ans = 0;
	for (int y = 0; y < N; ++y) {
		for (int x = 0; x <= y; ++x) {
			if (m1[P(y, x)]) ++ans;
		}
	}
	cout << ans << endl;
}

