int N,M;
int main()
{
	scanf("%d%d",&N,&M);
	for(int i=0;i<M;i++){
		int A,B,X;
		scanf("%d%d%d",&A,&B,&X);A--;B--;
		for(int j=A;j<=A+X;j++){
			board[j].push_back(P(B,B+(j-A)+1));
		}
	}
	int ans=0;
	for(int i=0;i<N;i++){
		int cou=0;
		sort(board[i].begin(),board[i].end());
		int start=-1;int end=-1;
		for(int j=0;j<board[i].size();j++){
			if(end<board[i][j].first){
				cou+=(end-start);
				start=board[i][j].first;
				end=board[i][j].second;
			}
			else{
				end=board[i][j].second;
			}
		}
		cou+=(end-start);
		ans+=cou;
	}
	printf("%d\n",ans);
	return 0;
}

