#include<cstdio>
#include<vector>
#include<algorithm>

#define rep(i,n) for(int i=0;i<(n);i++)

using namespace std;

struct triangle{
	int a,b,x;
	bool operator<(const triangle &T)const{
		if(a!=T.a) return a<T.a;
		if(b!=T.b) return b<T.b;
		return x<T.x;
	}
};

int main(){
	int n,m; scanf("%d%d",&n,&m);
	static triangle T[500000];
	rep(i,m){
		scanf("%d%d%d",&T[i].a,&T[i].b,&T[i].x);
		T[i].a--;
		T[i].b--;
	}
	sort(T,T+m);

	static short A[5000][5000];
	vector< pair<int,int> > del[5000]; // 各三角形の下端
	rep(i,m){
		if(i<m-1 && T[i].a==T[i+1].a && T[i].b==T[i+1].b) continue; // 始点が同じ三角形が複数あるなら一番大きいものだけ考える ( こうしないと後がうまくいかない )

		int a=T[i].a,b=T[i].b,x=T[i].x;
		A[a][b]=1;
		del[a+x].push_back(make_pair(b,x));
	}
	rep(i,n) sort(del[i].begin(),del[i].end());

	int ans=0;
	rep(i,n){
		static int next[5000];
		rep(j,i+1) next[j]=A[i][j];
		if(i>0){
			// 一行上の三角形を下ろしてくる
			rep(j,i){
				if(A[i-1][j]>0){
					next[j]=max<int>(next[j],A[i-1][j]+1); // 三角形のすそを一つ広げる
				}
			}
		}

		// 三角形に含まれる i 行目の釘を数える
		int now=0;
		rep(j,i+1){
			now=max<int>(now,next[j]);
			if(now>0) ans++;
			now=max(now-1,0);
		}

		// 下端に達した三角形を削除
		int p=0;
		rep(j,i){
			while(p<del[i].size() && del[i][p]<make_pair(j,next[j]-1)) p++;
			if(p>=del[i].size()) break;
			if(del[i][p]==make_pair(j,next[j]-1)) next[j]=0, p++;
		}
		// A[i][0..i] を更新
		rep(j,i+1) A[i][j]=max<int>(A[i][j],next[j]);
	}

	printf("%d\n",ans);

	return 0;
}

