int N, M;

void put(int a, int b, int x) {
  nails[b][a-b+1]++;
  nails[b+1][a-b]--;
  nails[b][a+x-b+1]--;
  nails[b+1][a+x-b+1]++;
  nails[b+x+1][a-b]++;
  nails[b+x+1][a-b+1]--;
}

void accumulate() {

  for(int x = 0; x <= N + 2; x++) for(int y = 1; y <= N + 2; y++) {
    nails[x][y] += nails[x][y-1];
  }

  for(int y = 0; y <= N + 2; y++) for(int x = 1; x <= N + 2; x++) {
    nails[x][y] += nails[x-1][y];
  }

  for(int sum = 0; sum <= N + 3; sum++) for (int x = 1; sum - x >= 0; x++) {
    nails[x][sum-x] += nails[x-1][sum-x+1];
  }
}

int count() { 
  int sum = 0;
  REP(y, N+1) {
    REP(x, N+1) {
      if (nails[x][y] > 0) sum++;
    }
  }
  return sum;
}

