			triangle[i][j + 1] += triangle[i][j];
		}
	}

	for(int i = 0; i < size; ++i) {
		for(int j = i + 1; j < size; ++j) {
			triangle[j][i] += triangle[j - 1][i];
		}
	}

	for(int i = 0; i < size; ++i) {
		for(int j = 1; i + j < size; ++j) {
			triangle[i + j][j] += triangle[i + j - 1][j - 1];
		}
	}

	int ans = 0;
	for(int i = 1; i <= n; ++i) {
		for(int j = 1; j <= i; ++j) {
			if(triangle[i][j] > 0) ++ans;
		}
	}

	cout << ans << endl;

	return EXIT_SUCCESS;
}

